CREATE TABLE clientes (
  id INT PRIMARY KEY,
  nombre VARCHAR(50),
  apellido VARCHAR(50),
  pais VARCHAR(50)
);


SELECT * FROM clientes

SELECT nombre, apellido, pais FROM clientes
WHERE "pais" = 'España'

CREATE TABLE productos (
  id INT PRIMARY KEY,
  nombre VARCHAR(250),
  descripcion VARCHAR(250),
  precio DECIMAL,
  stock INT
);



SELECT nombre FROM productos
ORDER BY nombre ASC

SELECT AVG(precio) FROM productos

SELECT nombre, stock, precio FROM productos
WHERE precio > 100

CREATE TABLE empleados (
  id INT PRIMARY KEY,
  nombre VARCHAR(50),
  apellido VARCHAR(50),
  salario DECIMAL,
  departamento VARCHAR(50),
  fecha_nacimiento DATE
);


SELECT nombre, salario FROM empleados
WHERE salario > 50000

SELECT nombre, apellido, departamento FROM empleados
WHERE departamento = 'ventas'

SELECT SUM(salario) FROM empleados

CREATE TABLE ventas (
  id INT PRIMARY KEY,
  producto_id INT,
  cliente_id INT,
  cantidad INT,
  fecha_venta DATE,
  FOREIGN KEY (producto_id) REFERENCES productos(id),
  FOREIGN KEY (cliente_id) REFERENCES clientes(id)
);

DROP TABLE ventas


SELECT producto_id, cantidad FROM ventas
WHERE cantidad > 100

SELECT nombre,apellido FROM empleados
WHERE nombre LIKE 'J%'

SELECT * FROM productos
WHERE nombre LIKE '%azul%'

SELECT nombre, stock FROM productos
WHERE stock < 10

SELECT productos.nombre, ventas.cantidad
FROM productos
INNER JOIN ventas ON productos.id = ventas.producto_id
WHERE ventas.cantidad BETWEEN 50 AND 100;

SELECT SUM(precio) FROM productos

SELECT productos.nombre, clientes.nombre
FROM ventas
JOIN productos ON ventas.producto_id = productos.id
JOIN clientes ON ventas.cliente_id = clientes.id;

SELECT nombre, apellido FROM empleados
WHERE fecha_nacimiento < '1980-01-01'

SELECT clientes.nombre, productos.nombre
FROM ventas
INNER JOIN clientes
  ON ventas.cliente_id = clientes.id
INNER JOIN productos
  ON ventas.producto_id = productos.id
WHERE EXTRACT(MONTH FROM fecha_venta ) = EXTRACT(MONTH FROM NOW())